import React, { useState } from "react";
import { Grid, Button, Select, Input } from "@material-ui/core";

const Orders = () => {
  const [orders, setOrders] = useState([
    {
      name: "T-Shirt",
      image:
        "data:image/jpeg;base64,",
      status: "pending",
    },
    {
      name: "Shoes",
      image:
        "data:image/jpeg;base64,",
      status: "cancelled",
    },
    {
      name: "Hat",
      image:
        "data:image/jpeg;base64,",
      status: "pending",
    },
    {
      name: "Jacket",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRd7JLYPeqE8TVg_F_gyLLwm3JuZ7UkAc99og&usqp=CAU",
      status: "complete",
    },
    {
      name: "Belt",
      image:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQvmcsR27Zq1orUNm3C9YQGYw37QSV4EQ-mXg&usqp=CAU",
      status: "pending",
    },
    {
      name: "Sunglasses",
      image:
        "data:image/jpeg;base64,",
      status: "pending",
    },
  ]);

  const [searchText, setSearchText] = useState("");
  const [orderFilter, setOrderFilter] = useState("all");
  const [sortOrder, setSortOrder] = useState("descending");

  const handleGoBackToCart = () => {
    window.history.back();
  };

  const handleSearch = (e) => {
    const searchText = e.target.value;
    setSearchText(searchText);
  };

  const handleOrderFilter = (e) => {
    const orderFilter = e.target.value;
    setOrderFilter(orderFilter);
  };

  const handleSort = (e) => {
    const sortOrder = e.target.value;
    setSortOrder(sortOrder);
  };

  const filteredOrders = orders.filter(
    (order) =>
      order.name.toLowerCase().includes(searchText.toLowerCase()) &&
      (orderFilter === "all" || order.status === orderFilter)
  );

  const sortedOrders = [...filteredOrders].sort((a, b) => {
    if (sortOrder === "ascending") {
      return a.price - b.price;
    } else {
      return b.price - a.price;
    }
  });

  return (
    <div>
      <div className="search-sort-filter-container">
        <Input
          type="text"
          placeholder="Search Orders"
          onChange={handleSearch}
        />
        <Select value={orderFilter} onChange={handleOrderFilter}>
          <option value="all">All</option>
          <option value="pending">Pending</option>
          <option value="complete">Complete</option>
          <option value="cancelled">Cancelled</option>
        </Select>
        <Select value={sortOrder} onChange={handleSort}>
          <option value="ascending">Sort: Low to High</option>
          <option value="descending">Sort: High to Low</option>
        </Select>
      </div>
      <Grid container spacing={3}>
        {sortedOrders.map((order) => (
          <Grid item xs={4} key={order.name}>
            <div className="order">
              <img src={order.image} alt={order.name} />
              <h4>{order.name}</h4>
              <h4>Status: {order.status.toUpperCase()}</h4>
            </div>
          </Grid>
        ))}
      </Grid>
      <div className="go-back-container">
        <Button
          variant="contained"
          color="primary"
          onClick={handleGoBackToCart}
        >
          Go Back To Cart
        </Button>
      </div>
    </div>
  );
};

export default Orders;
